import React, { Component } from 'react';
import axios from 'axios';

export default class Login extends Component {

  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);

    this.onChangePassword = this.onChangePassword.bind(this);

    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: '',

      password: '',

    users: []
    }
  }


  componentDidMount() {
          this.setState({
            username: this.props.match.params.username

          })

   }
  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    })
  }
  onChangePassword(e) {
    this.setState({
      password: e.target.value
    })
  }


  onSubmit(e) {
    e.preventDefault();//this will prevent the default html form submit behaviour from tking place

    const existinguser = {
      username: this.state.username,

      password: this.state.password,

    }

    console.log(existinguser);

  axios.post('http://localhost:5000/signups/find', existinguser)
     .then(res =>{
       if(res.data.length > 0){
         this.setState({
           users: res.data
         })
         console.log("here is the result:-"+this.state.users);
       }
     });
  // // console.log('/login/'+this.state.username);
  // //  window.location = '/login/'+this.state.username;
 }
  render() {
    return (
    <div>
    <form onSubmit={this.onSubmit}>
      <div className="form-group">
        <label>Username: </label>
         <input  type="text"
            required
            className="form-control"
            value={this.props.match.params.username}
            onChange={this.onChangeUsername}
            />
      </div>
      <div className="form-group">
        <label>Password: </label>
        <input
            type="text"
            className="form-control"
            value={this.state.password}
            onChange={this.onChangePassword}
            />
      </div>
      <div className="form-group">
        <input type="submit" value="Add New User" className="btn btn-primary" />
      </div>
      <div className="form-group">
        not a member?<a href="/signup">sign up here</a>      </div>
</form>
      </div>
    )
  }
}
