import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import Timer from 'react-compound-timer'

export default class DetailExercise extends Component {
  constructor(props) {
    super(props);
    this.timer = this.timer.bind(this);
    this.state = {
      username: '',
      taskname:'',
      description: '',
      duration: 0,
      date: new Date(),
      time:'',
      status:''
        }
  }

  componentDidMount() {
    axios.get('http://localhost:5000/exercises/'+this.props.match.params.id)
      .then(response => {
        this.setState({
          username: response.data.username,
            taskname: response.data.taskname,
          description: response.data.description,
          duration: Number(response.data.duration),
          date: new Date(response.data.date),
          time: response.data.time,
          status: response.data.status
        })

      })
      .catch(function (error) {
        console.log(error);
      })


  }
  timer() {
      return   Number(this.state.duration)*60000;
  }
  render() {
    return (
    <div>
      <h3>{this.state.username}</h3>
      <div>{this.state.taskname}</div>
      <div>{this.state.description}</div>
      <div>{this.state.duration}</div>
      <div>  <DatePicker
          selected={this.state.date}
          disabled
        /></div>
      <div>{this.state.time}</div>
      <div>{this.state.status}</div>
      <div>
<button onClick={this.timer()}>show timer</button>
      <Timer
  initialTime={Number(this.state.duration)*60000}
  direction="backward"
>
  {() => (
      <React.Fragment>
      <Timer.Days /> days
        <Timer.Hours /> hours
          <Timer.Minutes /> minutes
          <Timer.Seconds /> seconds
          <Timer.Milliseconds /> milliseconds
      </React.Fragment>
  )}
</Timer></div>
<Link to={"/task/"+this.props.match.params.users+"/"+this.props.match.params.id}>go back</Link>
    </div>
    )
  }
}
