import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import Timer from 'react-compound-timer'
import Jumbotron from 'react-bootstrap/Jumbotron';
export default class DetailExercise extends Component {
  constructor(props) {
    super(props);
   this.timer = this.timer.bind(this);
    this.state = {
      username: '',
      taskname:'',
      description: '',
      duration: 0,
      date: new Date(),
      time:'',
      status:''
        }
  }

  componentDidMount() {
    axios.get('http://localhost:5000/exercises/'+this.props.match.params.id)
      .then(response => {
        this.setState({
          username: response.data.username,
            taskname: response.data.taskname,
          description: response.data.description,
          duration: Number(response.data.duration),
          date: new Date(response.data.date),
          time: response.data.time,
          status: response.data.status
        })

      })
      .catch(function (error) {
        console.log(error);
      })


  }
  timer() {
    if(this.props.match.params.status === "in progress")
      return  Number(this.props.match.params.duration)*60000;
    else
    return 0;
  }
  render() {
    return (
    <div >
    <center>
      <h3>username:-{this.state.username}</h3>
      <div>Task Category:-{this.state.taskname}</div>
      <div>Project Name:-{this.state.description}</div>
      <div>Time Allocated:-{this.state.duration}</div>
      <div>End Date:-  <DatePicker
          selected={this.state.date}
          disabled
        /></div>

      <div>End Time:-{this.state.time}</div>
      <div>Status:-{this.state.status}</div>
      <div>
      Time Left(from allocated time):-
      <br/>
      <Timer
    initialTime={this.timer()}
    direction="backward"
    >
    {() => (
      <React.Fragment>

          <Timer.Days /> days
          <Timer.Hours /> hours
          <Timer.Minutes /> minutes
          <Timer.Seconds /> seconds
          <Timer.Milliseconds /> milliseconds
      </React.Fragment>
    )}
    </Timer>

</div>
<Link to={"/task/"+this.props.match.params.users+"/"+this.props.match.params.id}>go back</Link>
</center>
    </div>
    )
  }
}
