import React, { Component } from 'react';
import axios from 'axios';

export default class Signup extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.onChangeTelephone = this.onChangeTelephone.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: '',
      email: '',
      password: '',
      telephone:'',
    //  users: []
    }
  }
//react lifecycle method
  //componentDidMount() {
  //   axios.get('http://localhost:5000/users/')
  //     .then(response => {
  //       if (response.data.length > 0) {
  //         this.setState({
  //           users: response.data.map(user => user.username),
  //           username: response.data[0].username
  //         })
  //       }
  //     })
  //     .catch((error) => {
  //       console.log(error);
  //     })
  //
  // }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    })
  }

  onChangeEmail(e) {
    this.setState({
      email: e.target.value
    })
  }

  onChangePassword(e) {
    this.setState({
      password: e.target.value
    })
  }

  onChangeTelephone(e) {
    this.setState({
      telephone: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();//this will prevent the default html form submit behaviour from tking place

    const newuser = {
      username: this.state.username,
      email: this.state.email,
      password: this.state.password,
      telephone: this.state.telephone
    }

    console.log(newuser);

    axios.post('http://localhost:5000/signups/add', newuser)
      .then(res => console.log(res.data));
   console.log('/login/'+this.state.username);
    window.location = '/login/'+this.state.username;
  }

  render() {
    return (
    <div>
      <h3>Create New Account</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group">
          <label>Username: </label>
           <input  type="text"
              required
              className="form-control"
              value={this.state.username}
              onChange={this.onChangeUsername}
              />
        </div>
        <div className="form-group">
          <label>Email: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.email}
              onChange={this.onChangeEmail}
              />
        </div>
        <div className="form-group">
          <label>Password: </label>
          <input
              type="text"
              className="form-control"
              value={this.state.password}
              onChange={this.onChangePassword}
              />
        </div>
        <div className="form-group">
          <label>Telephone: </label>
          <input
              type="text"
              className="form-control"
              value={this.state.telephone}
              onChange={this.onChangeTelephone}
              />
        </div>

        <div className="form-group">
          <input type="submit" value="Add New User" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
